jQuery(document).ready(function(){TRUEMAN_STORAGE.media_frame=null;TRUEMAN_STORAGE.media_link="";jQuery(".trueman_media_selector").on("click",function(a){trueman_show_media_manager(this);a.preventDefault();return false})});function trueman_show_media_manager(a){TRUEMAN_STORAGE.media_link=jQuery(a);if(TRUEMAN_STORAGE.media_frame){TRUEMAN_STORAGE.media_frame.open();return false}TRUEMAN_STORAGE.media_frame=wp.media({title:TRUEMAN_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:TRUEMAN_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:TRUEMAN_STORAGE.media_link.data("update"),close:true}});TRUEMAN_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+TRUEMAN_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(TRUEMAN_STORAGE.media_link.data("multiple")===true){TRUEMAN_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=TRUEMAN_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});TRUEMAN_STORAGE.media_frame.open();return false};